import React,{useState,useEffect} from "react";
import '../styles/Formm.css';
import axios from "axios";

//let latitude,longitude;
function LocationForm() {
  const [position, setPosition] = useState({latitude: null, longitude: null});

  function handleInputChange(event) {
    setPosition({
      ...position,
      [event.target.name]: event.target.value
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    console.log(position);
  }

  if (navigator.geolocation) {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setPosition({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude
        });
      },
      (error) => {
        console.log(error);
      }
    );
  } else {
    console.log("Geolocation is not supported by this browser.");
  }
}



const  Myform=()=>{
  const [userData,setUserData]=useState({
    name:"",
    email:"",
    phone:"",
    aspect:"",
  });

  const [plantsData,setPlantsData]=useState({
    plantname:"",
    specie:"",
    purpose:"",
    spacing:"",
    survivalRate:"",
    date:"",
    picture:""
  });

  const [locationData,setLocationData]=useState({
    typedLocation:"",
    latitude:"",
    longitude:""
  });

  const {name,phone,email,aspect}=userData;
  const {plantname,specie,purpose,spacing,survivalRate,date,ownerShip,picture}=plantsData;
  const {typedLocation,latitude,longitude}=locationData;
  
  
  const handleInputs=(e)=>{
    setUserData({...userData,[e.target.name]:e.target.value});
    setPlantsData({...plantsData,[e.target.name]:e.target.value});
    setLocationData({...locationData,[e.target.name]:e.target.value});
  }
  const onSubmit=async(e)=>{
    e.preventDefault();
    const newUser={
      name:name,
      phone:phone,
      email:email,
      aspect:aspect,
    }
    const newPlant={
      plantname:plantname,
      specie:specie,
      purpose:purpose,
      spacing:spacing,
      survivalRate:survivalRate,
      date:date,
      ownerShip:ownerShip,
      picture:picture,
    }
    const newLocation={
      typedLocation:typedLocation,
      longitude:longitude,
      latitude:latitude,
    }
    const config={
      headers:{
        "Content-Type":"application/json",
      },
    };
    try{
      const body=JSON.stringify(newUser);
      const body1=JSON.stringify(newPlant);
      const body2=JSON.stringify(newLocation);
      await axios.post("/user",body2,config);
      await axios.post("/user",body1,config);
      await axios.post("/user",body,config);
      setUserData({
        name:"",
        phone:"",
        email:"",
        aspect:"",
      });
      setPlantsData({
        plantname:"",
        specie:"",
        purpose:"",
        spacing:"",
        survivalRate:"",
        date:"",
        ownerShip:"",
        picture:"",
      });
      setLocationData({
        typedLocation:"",
        latitude:"",
        longitude:"",
      })
      window.location.reload();
    }catch(err){
      console.error("Error:",err.response.data);
    }
  }

  return (
        <div className="Form">
          <div className="testbox">
          <form onSubmit={(e)=>onSubmit(e)}>
            <div className="banner">
              <h1 className='header'>Performa for Agroforestry and Farm Forestry</h1>
            </div>
            <div className="item">
              <p>Location</p>
              <div className="name-item">
                <input className="input" type="text" name="typedLocation"
                value={typedLocation}
                onChange={(e)=>handleInputs(e)}
                
                placeholder="Location" required/>
              </div>
            </div>
            <div className="item">
                <p>Name:</p>
                <div className="name-item">
                  <input type="text" className="input" name="name"
                  value={name}
                  onChange={(e)=>handleInputs(e)}
                  
                  placeholder="Enter name" required/>
              </div>
            </div>
            <div className="item">
              <p>Email</p>
              <input type="email" className="input" name="email" 
              value={email}
              onChange={(e)=>handleInputs(e)}
              
              placeholder='email' required/>
            </div>
            <div className="item">
              <p>Phone</p>
              <input type="text" name="phone" 
              value={phone}
              onChange={(e)=>handleInputs(e)}
              
              placeholder="92 333000000" required/>
            </div>
            <div className="item">
            <button onClick={function getLoc(){
                navigator.geolocation.getCurrentPosition(showPosition);
                function showPosition(position){
                console.log("Latitude: " + position.coords.latitude);
              }
              }} name="location">latitude</button>
              value={latitude}
            </div>
            <div className="item">
              <button onClick={function getLoc(){
                navigator.geolocation.getCurrentPosition(showPosition);
                function showPosition(position){
                console.log("Longitude: " + position.coords.longitude);
              }
              }} name="location">longitude</button>
              value={longitude}
            </div>
            <div className="item">
              <p>Aspect:</p>
              <input type="text" className="input" name="aspect" 
              value={aspect}
              onChange={(e)=>handleInputs(e)}
              
              placeholder='Aspect' required/>
            </div>
            <div className="question">
              <p>OwnerShip:</p>
              <div className="question-answer">
                <div>
                  <input className="radio" type="radio" name="ownerShip"
                  value={ownerShip}
                  onChange={(e)=>handleInputs(e)}/>
                  <label htmlFor="radio" className="radio1"><span>Community</span></label>
                </div>
                <div>
                  <input type="radio" name="ownerShip"
                  value={ownerShip}
                  onChange={(e)=>handleInputs(e)}/>
                  <label htmlFor="radio" className="radio2"><span>Individual</span></label>
                </div>
              </div>
            </div>
            <div className="question">
              <p>Purpose of AF/FF:</p>
              <div className="question-answer radioopt">
                <div>
                  <input type="radio"  name="purpose"
                  value={purpose}
                  onChange={(e)=>handleInputs(e)}
                  
                  id="radio_4"/>
                  <label htmlFor="radio_4"  className="radio3"><span>Fuel Wood</span></label>
                </div>
                <div>
                  <input type="radio"  name="purpose"
                   value={purpose}
                   onChange={(e)=>handleInputs(e)}
                   
                  id="radio_5"/>
                  <label htmlFor="radio_5" className="radio4"><span>Fodder</span></label>
                </div>
                <div className="timber">
                  <input type="radio" name="purpose"
                  value={purpose}
                  onChange={(e)=>handleInputs(e)}
                  
                  id="radio_6"/>
                  <label htmlFor="radio_6" className="radio5"><span>Timber</span></label>
                </div>
                <div className="others">
                    <label ><span>Other(please specify)</span></label>
                    <input type="text" className="input1" id="radio_6" name="other"
                     //value={other}
                     onChange={(e)=>handleInputs(e)}/>
                </div>
                <div className="item">
                    <p>Species Of Plants Provided:</p>
                    <input type="text" className="input" name="specie" 
                    value={specie}
                    onChange={(e)=>handleInputs(e)}
                    
                    required/>
                </div>
                <div className="item">
                    <p>Spacing:</p>
                    <input type="text" className="input" name="spacing" 
                    value={spacing}
                    onChange={(e)=>handleInputs(e)}
                    
                     required/>
                </div>
                <div className="item">
                    <p>Survival Rate:</p>
                    <input type="text" className="input" name="survivalRate" 
                     value={survivalRate}
                     onChange={(e)=>handleInputs(e)}
                   
                     required/>
                </div>
                <div className="item">
                    <p>Suggestions:</p>
                    <input type="text" className="input" name="suggestions" 
                    //value={user.suggestions}
                    onChange={(e)=>handleInputs(e)}
                    
                    required/>
                </div>
              </div>
            </div>
            <div className="item1">
                <p>Date of Planting:</p>
                <input type="date" name="date"
                value={date}
                onChange={(e)=>handleInputs(e)}
                required/>
                <i className="fas fa-calendar-alt"></i>
            </div>
            <div >
                <input  type="file" className="input" name="picture"
                value={picture}
                onChange={(e)=>handleInputs(e)} />
                <i className="fas fa-calendar-alt"></i>
            </div>
            <div className="btn-block btn">
              <button type="submit" href="#">Submit</button>
            </div>
          </form>
        </div>
        </div>
      );

}
export default Myform;
