import React,{useState,useEffect} from "react";
import '../styles/Formm.css';
import axios from "axios";

const getLocation=()=>{
  navigator.geolocation.getCurrentPosition((position)=>{
    console.log(position);
    var {latitude}=position.coords;
    var {longitude}=position.coords;
    console.log(latitude,longitude);
  },()=>{
    alert("could not get your postion");
  })
}


const  Myform=()=>{
    const [user,setUser]=useState({
      name:"",email:"",aspect:"",phone:"",location:"",latitude:"",longitude:"",ownership:"",purpose:"",specie:"",spacing:"",survivalRate:"",suggestions:"",date:"",pic:"",other:""
    });
    
    const {name,email,aspect,phone,location,latitude,longitude,ownership,purpose,specie,spacing,survivalRate,suggestions,date,pic,other}=user;
    
    let name1,value;
    const handleInputs=(e)=>{
      console.log(e);
      name1=e.target.name;
      value=e.target.value;
      setUser({...user,[name1]:value});
      console.log(user);
    }
    const onSubmit=async(e)=>{
      e.preventDefault();
      const newUser={
        name:name,
        email:email,
        aspect:aspect,
        phone:phone,
        location:location,
        latitude:latitude,
        longitude:longitude,
        ownership:ownership,
        purpose:purpose,
        specie:specie,
        spacing:spacing,
        survivalRate:survivalRate,
        suggestions:suggestions,
        date:date,
        pic:pic,
        other:other,

      };
      const config={
        header:{
          "Content-Type":"application/json",
        },
      };
      try{
        const body=JSON.stringify(newUser);
        await axios.post("/user",body,config);
        setUser({
          name:"",
          email:"",
          aspect:"",
          phone:"",
          location:"",
          latitude:"",
          longitude:"",
          ownership:"",
          purpose:"",
          specie:"",
          spacing:"",
          survivalRate:"",
          suggestions:"",
          date:"",
          pic:"",
          other:"",
      });
      window.location.reload();
      }catch(err){
        console.error("error",err.response.data);
      }
    }

    return (
        <div className="Form">
          <div className="testbox">
          <form onSubmit={(e)=>onSubmit(e)}>
            <div className="banner">
              <h1 className='header'>Performa for Agroforestry and Farm Forestry</h1>
            </div>
            <div className="item">
              <p>Location</p>
              <div className="name-item">
                <input className="input" type="text" name="location"
                value={user.location}
                onChange={(e)=>handleInputs(e)}
                
                placeholder="Location" required/>
              </div>
            </div>
            <div className="item">
                <p>Name:</p>
                <div className="name-item">
                  <input type="text" className="input" name="name"
                  value={user.name}
                  onChange={(e)=>handleInputs(e)}
                  
                  placeholder="Enter name" required/>
              </div>
            </div>
            <div className="item">
              <p>Email</p>
              <input type="email" className="input" name="email" 
              value={user.email}
              onChange={(e)=>handleInputs(e)}
              
              placeholder='email' required/>
            </div>
            <div className="item">
              <p>Phone</p>
              <input type="text" name="phone" 
              value={user.phone}
              onChange={(e)=>handleInputs(e)}
              
              placeholder="92 333000000" required/>
            </div>
            <div className="item">
              <p>GPS Coordinates:</p>
              <button onClick={getLocation()}>Location</button>
            </div>
            <div className="item">
              <p>Aspect:</p>
              <input type="text" className="input" name="aspect" 
              value={user.aspect}
              onChange={(e)=>handleInputs(e)}
              
              placeholder='Aspect' required/>
            </div>
            <div className="question">
              <p>OwnerShip:</p>
              <div className="question-answer">
                <div>
                  <input className="radio" type="radio" name="ownerShip"
                  value="Community"
                  onChange={(e)=>handleInputs(e)}/>
                  <label htmlFor="radio" className="radio1"><span>Community</span></label>
                </div>
                <div>
                  <input type="radio" name="ownerShip1"
                  value="individual"
                  onChange={(e)=>handleInputs(e)}/>
                  <label htmlFor="radio" className="radio2"><span>Individual</span></label>
                </div>
              </div>
            </div>
            <div className="question">
              <p>Purpose of AF/FF:</p>
              <div className="question-answer radioopt">
                <div>
                  <input type="radio"  name="purpose1"
                  value={user.purpose}
                  onChange={(e)=>handleInputs(e)}
                  
                  id="radio_4"/>
                  <label htmlFor="radio_4"  className="radio3"><span>Fuel Wood</span></label>
                </div>
                <div>
                  <input type="radio"  name="purpose2"
                   value={user.purpose}
                   onChange={(e)=>handleInputs(e)}
                   
                  id="radio_5"/>
                  <label htmlFor="radio_5" className="radio4"><span>Fodder</span></label>
                </div>
                <div className="timber">
                  <input type="radio" name="purpose3"
                  value={user.purpose}
                  onChange={(e)=>handleInputs(e)}
                  
                  id="radio_6"/>
                  <label htmlFor="radio_6" className="radio5"><span>Timber</span></label>
                </div>
                <div className="others">
                    <label ><span>Other(please specify)</span></label>
                    <input type="text" className="input1" id="radio_6" name="other"
                     value={user.other}
                     onChange={(e)=>handleInputs(e)}/>
                </div>
                <div className="item">
                    <p>Species Of Plants Provided:</p>
                    <input type="text" className="input" name="specie" 
                    value={user.specie}
                    onChange={(e)=>handleInputs(e)}
                    
                    required/>
                </div>
                <div className="item">
                    <p>Spacing:</p>
                    <input type="text" className="input" name="spacing" 
                    value={user.spacing}
                    onChange={(e)=>handleInputs(e)}
                    
                     required/>
                </div>
                <div className="item">
                    <p>Survival Rate:</p>
                    <input type="text" className="input" name="survivalRate" 
                     value={user.survivalRate}
                     onChange={(e)=>handleInputs(e)}
                   
                     required/>
                </div>
                <div className="item">
                    <p>Suggestions:</p>
                    <input type="text" className="input" name="suggestions" 
                    value={user.suggestions}
                    onChange={(e)=>handleInputs(e)}
                    
                    required/>
                </div>
              </div>
            </div>
            <div className="item1">
                <p>Date of Planting:</p>
                <input type="date"
                value={user.date}
                onChange={(e)=>handleInputs(e)}
                
                name="date" required/>
                <i className="fas fa-calendar-alt"></i>
            </div>
            <div >
                <input  type="file" className="input"
                value={user.pic}
                onChange={(e)=>handleInputs(e)}
                
                name="pic" />
                <i className="fas fa-calendar-alt"></i>
            </div>
            <div className="btn-block btn">
              <button type="submit" href="#">Submit</button>
            </div>
          </form>
        </div>
        </div>
      );

}
export default Myform;
